import React, { useState } from 'react';
import './HydrationBar.css';
import NavigationBar from './NavigationBar';
import App from '../App';

const HydrationBar = () => {
  const [goBack, setGoBack] = useState(false);
  const hydrationGoal = localStorage.getItem('hydrationGoal') || '0';
  const [showApp, setShowApp] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [isAddRemoveVisible, setIsAddRemoveVisible] = useState(false);
  const hydrationOptions = ['Water', 'Lemonade', 'Gatorade', 'Coconut Water', 'Sports Drinks', 'Electrolyte Water', 'Energy Drinks', 'Protein Shakes'];

  const [hydrationItems, setHydrationItems] = useState(
    JSON.parse(localStorage.getItem('hydrationItems')) || [
      { name: 'Water', value: '' },
      { name: 'Lemonade', value: '' },
      { name: 'Gatorade', value: '' },
      { name: 'Coconut Water', value: '' },
      { name: 'Sports Drinks', value: '' },
      { name: 'Electrolyte Water', value: '' },
      { name: 'Energy Drinks', value: '' },
      { name: 'Protein Shakes', value: '' },
    ]
  );
  const [totalHydration, setTotalHydration] = useState(parseFloat(localStorage.getItem('totalHydration')) || 0);

  const fetchNutritionData = async (hydrationItems) => {
    const apiKey = 'NZtR8gEN66zhw/pB5XB/6g==c8vpe58EveAhtGNo'; // Replace with your API key
    const query = `${hydrationItems.value}L ${hydrationItems.name}`;
    try {
      const response = await fetch(`https://api.api-ninjas.com/v1/nutrition?query=${query}`, {
        headers: {
          'X-Api-Key': apiKey,
        },
      });
      if (!response.ok) {
        throw new Error('Network response was not ok');
      }
      const data = await response.json();
      console.log('Fetched Nutrition Data:', data); // Check the fetched data from the API

      // Store the API response in local storage
      localStorage.setItem('nutritionData', JSON.stringify(data));

      return data;
    } catch (error) {
      console.error('Error fetching nutrition data:', error);
      // Handle errors as necessary
      return null;
    }
  };



  const handleBack = () => {
    setGoBack(true);
  };

  const handleEdit = () => {
    setIsEditing(true);
    setIsAddRemoveVisible(true);
  };

  const handleSave = () => {
    setIsEditing(false);
    setIsAddRemoveVisible(false);
    localStorage.setItem('hydrationItems', JSON.stringify(hydrationItems));
    localStorage.setItem('totalHydration', calculateTotalHydration());
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const foodItems = hydrationItems.map((item) => item.name);
    const nutritionDataPromises = foodItems.map((foodItem) => fetchNutritionData(foodItem));
    const nutritionData = await Promise.all(nutritionDataPromises);
    console.log('Nutrition Data:', nutritionData); // Handle the received nutrition data as required

    // Update the calorie breakdown section in App.js
    const totalCalories = nutritionData.reduce((acc, data) => {
      const { calories } = data;
      return acc + calories;
    }, 0);

    const totalCarbohydrate = nutritionData.reduce((acc, data) => {
      const { carbohydrate } = data;
      return acc + carbohydrate;
    }, 0);

    const totalProtein = nutritionData.reduce((acc, data) => {
      const { protein } = data;
      return acc + protein;
    }, 0);

    const totalFats = nutritionData.reduce((acc, data) => {
      const { fats } = data;
      return acc + fats;
    }, 0);

    // Update the state or do any necessary processing with the calculated data
    console.log('Total Calories:', totalCalories);
    console.log('Total Carbohydrate:', totalCarbohydrate);
    console.log('Total Protein:', totalProtein);
    console.log('Total Fats:', totalFats);
  };

  const handleHydrationChange = (e, index) => {
    const { value } = e.target;
    const newHydrationItems = [...hydrationItems];
    const totalHydration = calculateTotalHydration() - parseFloat(hydrationItems[index].value) + parseFloat(value);
    if (totalHydration > parseFloat(hydrationGoal)) {
      alert('Total hydration count exceeds the goal. Please adjust the values.');
      return;
    }
    newHydrationItems[index].value = value;
    setHydrationItems(newHydrationItems);
    localStorage.setItem('totalHydration', totalHydration);
  };

  const handleNameChange = (e, index) => {
    const newHydrationItems = [...hydrationItems];
    newHydrationItems[index].name = e.target.value;
    setHydrationItems(newHydrationItems);
  };

  const handleAdd = () => {
    const newHydrationItems = [
      ...hydrationItems,
      { name: 'Water', value: '' },
    ];
    setHydrationItems(newHydrationItems);
  };

  const handleRemove = (index) => {
    const newHydrationItems = [...hydrationItems];
    newHydrationItems.splice(index, 1);
    setHydrationItems(newHydrationItems);
  };

  const calculateTotalHydration = () => {
    let totalHydration = 0;
    if (hydrationItems) {
      hydrationItems.forEach((item) => {
        totalHydration += parseFloat(item.value) || 0;
      });
    }
    return totalHydration;
  };

  const handleCancel = () => {
    alert('You have canceled the goal setting.');
  };

  if (goBack) {
    return <App />;
  }

  // Rest of the code remains the same

  return (
    <div className="SetGoal">
      <button className="back-button" onClick={handleBack}>
        Back
      </button>
      <h1>Hydration Info</h1>
      <div className="container">
        <form onSubmit={handleSubmit}>
          <div className="hydration-form">
            <h4>Hydration Breakdown:</h4>
            {hydrationItems.map((item, index) => (
              <div key={index} className="item-container">
                <label className="item-label">
                  <select
                    className="dropdown"
                    value={item.name}
                    onChange={(e) => handleNameChange(e, index)}
                    disabled={!isEditing}
                  >
                    {hydrationOptions.map((option, optionIndex) => (
                      <option key={optionIndex} value={option}>
                        {option}
                      </option>
                    ))}
                  </select>
                </label>
                <input
                  className="item-input"
                  type="number"
                  placeholder="L"
                  value={item.value}
                  onChange={(e) => handleHydrationChange(e, index)}
                  disabled={!isEditing}
                />
              </div>
            ))}
            {isAddRemoveVisible && (
              <div className="button-container">
                <button className="add-button" type="button" onClick={handleAdd}>
                  Add Item
                </button>
                <button className="remove-button" type="button" onClick={handleRemove}>
                  Remove Item
                </button>
              </div>
            )}
          </div>
        </form>
        <div className="total-hydration">Total hydration count: {calculateTotalHydration()} L</div>
        <div className="edit-section">
          {!isEditing ? (
            <button className="edit-button" onClick={handleEdit}>
              Edit✏️
            </button>
          ) : (
            <button className="save-button" onClick={handleSave}>
              Save💾
            </button>
          )}
        </div>
      </div>
      <div className="goal-section">
        <p>Goal for today: {hydrationGoal} L</p>
      </div>
      <NavigationBar />
    </div>
  );
};

export default HydrationBar;
